import React, { ChangeEvent, useRef, useState } from 'react';
import logo from './logo.svg';
import './App.css';

const fs = require('fs'); // File System | Node.js
const axios = require('axios'); // HTTP client
const FormData = require('form-data'); // Readable "multipart/form-data" streams

const api_key = '2b10189SmpQJ3XHmESgf2Hz9k'

function App() {
  const inputFile = useRef<HTMLInputElement | null>(null);
  const [selectedImage, setSelectedImage] = useState<string | null>(null);

  const onUpload = () => {
    // `current` points to the mounted file input element
    if (inputFile.current) {
      inputFile.current.click();
    }
  };

  useEffect(() => {
    // POST request using fetch inside useEffect React hook
    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: 'React Hooks POST Request Example' })
    };
    fetch('https://reqres.in/api/posts', requestOptions)
        .then(response => response.json())
        .then(data => setPostId(data.id));

// empty dependency array means this effect will only run once (like componentDidMount in classes)
}, [selectedImage]);

  const onIdentify = () => {
    (async () => {
      let form = new FormData();

      form.append('organs', 'flower');
      form.append('images', fs.createReadStream(image_1));

      // form.append('organs', 'leaf');
      // form.append('images', fs.createReadStream(image_2));

      try {
        const { status, data } = await axios.post(
          'https://my-api.plantnet.org/v2/identify/all?api-key=YOUR-PRIVATE-API-KEY-HERE',
          form, {
          headers: form.getHeaders()
        }
        );

        console.log('status', status); // should be: 200
        console.log('data', require('util').inspect(data, false, null, true)); // should be: read "Step 6" below
      } catch (error) {
        console.error('error', error);
      }
    })();
  };

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event && event.target) {
      let input = event.target;
      var fReader = new FileReader();
      if (input.files) {
        fReader.readAsDataURL(input.files[0]);
        fReader.onloadend = (event) => {
          if (event && event.target) {
            setSelectedImage(event.target.result as string);
          }
        }
      }
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <img src={selectedImage ?? ""} className="App-logo" alt="logo" />
          <br/>
          <div>
            <input type='file' id='file' ref={inputFile} onChange={handleChange} style={{ display: 'none' }} accept="image/*" />
            <button onClick={onUpload}>Upload Image</button>
            <button onClick={onIdentify}>Identify</button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
