//         .then(data => setPostId(data.id));
// 
//   }, []);

  const onIdentify = () => {
    (async () => {
      let form = new FormData();

      form.append('organs', 'flower');
      form.append('images', fs.createReadStream(image_1));

      // form.append('organs', 'leaf');
      // form.append('images', fs.createReadStream(image_2));
