      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: 'React Hooks POST Request Example' })
      };

      try {
        fetch('https://my-api.plantnet.org/v2/identify/all?api-key=' + api_key, requestOptions)
          .then(response => response.json())
          .then(data => setPostId(data.id));

        // const { status, data } = await axios.post(
        //   'https://my-api.plantnet.org/v2/identify/all?api-key=YOUR-PRIVATE-API-KEY-HERE',
        //   form, {
        //   headers: form.getHeaders()
        // }
        // );

        console.log('status', status); // should be: 200
        console.log('data', require('util').inspect(data, false, null, true)); // should be: read "Step 6" below
      } catch (error) {
        console.error('error', error);
      }
    })();
  };

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    if (event && event.target) {
      let input = event.target;
      var fReader = new FileReader();
      if (input.files) {
        fReader.readAsDataURL(input.files[0]);
        fReader.onloadend = (event) => {
          if (event && event.target) {
            setSelectedImage(event.target.result as string);
          }
        }
      }
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <img src={selectedImage ?? ""} className="App-logo" alt="logo" />
          <br/>
          <div>
            <input type='file' id='file' ref={inputFile} onChange={handleChange} style={{ display: 'none' }} accept="image/*" />
            <button onClick={onUpload}>Upload Image</button>
            <button onClick={onIdentify}>Identify</button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
